.red:hover, .red:focus, .red:active, .show > .red { background-color: var(--red)!important; box-shadow: none;}
.green:hover, .green:focus, .green:active, .show > .green { background-color: var(--green)!important; box-shadow: none;}
.blue:hover, .blue:focus, .blue:active, .show > .blue { background-color: var(--blue)!important; box-shadow: none;}

.show > .btn-secondary.dropdown-toggle:focus {
    box-shadow: none;
}
