.dropdown-toggle input::placeholder { color: #ffff; opacity: 1;}
.onclick::placeholder {opacity: 0.5!important;}
.dropdown-toggle input::focus-visible {outline:0px!important; box-shadow: none !important;}
.search__input:focus { background-color: #E7E7E7;}

.changeSize { width: 400px;}

.search__button {
    width: 69px;
    background-color: #E7E7E7;
    border: none;
    padding: 0;
}

.search__button svg { height: 32px; }
.btn-outline-secondary:hover { background-color: #E7E7E7; color: #000000;}


#search-results { 
    display: grid;
    grid-template-columns: repeat(3, 380px);
    grid-template-rows: auto;
    justify-content: space-between;
    margin: 30px 0px;
}

article {
    height: 385px;
    width: 400px;
    background-color: #E7E7E7;
    border-radius: 5px;
    margin: 20px 0px
}

article .recipe__image { height: 160px; width: 100%; background-color: #C7BEBE; border-radius: 5px 5px 0px 0px;}

.recipe__info { padding: 1em; box-sizing: border-box;}

.recipe__title { display: flex; justify-content: space-between; align-items: center;}
.recipe__title h2, .recipe__title span { 
    font-family: Lato;
    font-size: 18px;
    line-height: 22px;
    font-weight: 400;
    margin: 0;
}

.recipe__time { display: flex; align-items: center; min-width: 70px;}
.recipe__time img { margin : 0px 5px}

.recipe__meta { display: flex; font-family: Lato; font-size: 12px; margin-top: 20px;}
.recipe__meta > div { width: 50%;}
.recipe__meta p { 
    max-height: 100%; 
    line-height: 12px; 
    overflow: hidden; 
    text-overflow: ellipsis; 
    overflow: hidden;
    display: -webkit-box;
    -webkit-line-clamp: 9;
    -webkit-box-orient: vertical; }

